import * as fetchApi from "../helpers/Api/fetchDataApi.js";
import * as functionallity from "../helpers/functionallity/updateTeams";
import * as helperApi from "../"
import store from "../index";
import alliances from "../reducers/alliances.js";
// action Creators
export const getTeams = () => async (dispatch) => {
  try {
    const data = await fetchApi.fetchTeams(store.getState().token);
    dispatch({ type: "FETCH_ALL_TEAMS", payload: data });
  } catch (err) {
    console.log(err);
  }
};
export const AddTeam = (teams, NewTeamName) => async (dispatch) => {
  try {
    const newTeam = await functionallity.addNewTeam(NewTeamName);
    dispatch({ type: "UPDATE_TEAMS", payload: teams.concat(newTeam) });
  } catch (err) {
    console.log(err);
  }
};
export const deleteTeam = (team, teams) => async (dispatch) => {
  try {
    const teamsAfterDelete = await functionallity.deleteTeam(team, teams);
    dispatch({ type: "UPDATE_TEAMS", payload: teamsAfterDelete });
  } catch (err) {
    console.log(err);
  }
};
export const addTeamToAlliance =
  (team, alliance, teams) => async (dispatch) => {
    console.log(teams, "add Team To Alliance Functionallity ");
    try {
      const teamsUpdated = await functionallity.addTeamToAlliance(
        team,
        alliance,
        teams
      );
      console.log(teamsUpdated, "teamsUpdatedteamsUpdatedteamsUpdated");
      dispatch({ type: "UPDATE_TEAMS", payload: teamsUpdated });
    } catch (err) {
      console.log(err);
    }
  };
