import axios from "axios";
import store from "../../index";
import alliances from "../../reducers/alliances";
const addTeamToAllianceApi = async (team, alliance) => {
  var data = JSON.stringify({
    alliance: alliance.id,
  });
  const team_url = `http://cms.avertra.com/teams/${team.id}`;

  var config = {
    method: "put",
    url: team_url,
    headers: {
      Authorization: "Bearer " + store.getState().token,
      "Content-Type": "application/json",
    },
    data: data,
  };

  try {
    const teamUpdated = await axios(config);
    console.log(teamUpdated, "added !!");
    return teamUpdated.data;
  } catch (err) {
    console.error(err);
  }
};

const getTeamBelongToThisAlliance = async (alliance) => {
  var config = {
    method: "get",
    url: `http://cms.avertra.com/teams?alliance.id=${alliance.id}`,
    headers: {
      Authorization: "Bearer " + store.getState().token,
    },
  };
  try {
    const resp = await axios(config);
    return resp.data;
  } catch (err) {
    console.error(err);
    return [];
  }
};
const createNewTeam = async (NewTeamName) => {
  var data = JSON.stringify({
    Name: NewTeamName,
  });
  var config = {
    method: "post",
    url: "http://cms.avertra.com/teams",
    headers: {
      Authorization: "Bearer " + store.getState().token,
      "Content-Type": "application/json",
    },
    data: data,
  };
  try {
    const resp = await axios(config);
    return resp.data;
  } catch (err) {
    console.error(err);
    return {};
  }
};
const deleteTeam = async (team) => {
  var config = {
    method: "delete",
    url: `http://cms.avertra.com/teams/${team.id}`,
    headers: {
      Authorization: "Bearer " + store.getState().token,
    },
  };
  try {
    await axios(config);
  } catch (err) {
    console.error(err);
    return {};
  }
};

export {
  addTeamToAllianceApi,
  getTeamBelongToThisAlliance,
  createNewTeam,
  deleteTeam,
};
