import React, { useState, useEffect } from "react";
import Employee from "./Employee";
import Team from "./Team";
import Alliance from "./Alliance";
import { useSelector, useDispatch } from "react-redux";
import TextField from "@material-ui/core/TextField";
import { Button, Card } from "@mui/material";
import { AddTeam } from "../actions/teams";
import Grid from "@mui/material/Grid";
import GridList from "@material-ui/core/GridList";
import Slider from "react-slick";

// Import css files
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "../styles/App.css";
function DragDrop() {
  const dispatch = useDispatch();
  const teams = useSelector((state) => {
    return state.teams;
  });
  var t = teams.filter((t) => {
    return t.alliance === undefined;
  });
  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  console.log(teams, "TEAMS Drag and DROP");
  console.log(t, "t Drag and DROP");
  const [searchField, setsearchField] = useState("");
  const [NewTeamName, setNewTeamName] = useState("");
  const employees = useSelector((state) => state.employees);
  const alliances = useSelector((state) => state.alliances);
  var filteredEmployees = employees.filter((emp) =>
    emp["Name"].toLowerCase().includes(searchField.toLowerCase())
  );

  filteredEmployees = filteredEmployees
    .concat(filteredEmployees)
    .concat(filteredEmployees);
  console.log(filteredEmployees, "eeeeeeeeeee");
  const players = [
    {
      name: "Sura",
      image:
        "https://www.google.com/search?q=cat+images&rlz=1C1GCEU_enJO972JO972&tbm=isch&source=iu&ictx=1&fir=--16yG3LYPKNHM%252CNlpGXv_hAl-MuM%252C_%253B_tJ9zISYcJ92GM%252CrzUo6RAz-vdsGM%252C_%253Baiul5-jr8DbWEM%252C6MsMJjk6eGCktM%252C_%253B9eZVJa6ncTJ-VM%252CTmUkLtkPkKEi5M%252C_%253Br8gSbpVXU-d6FM%252CZx4TpH0a6gviqM%252C_%253BNleIXKNfiJGUxM%252CFpFnzuctxfxSjM%252C_%253BeB5xN0Rn9Ei4BM%252CKhobAKLF-PpqJM%252C_%253BT4DSUl1hwK8BHM%252C4R-NKAFxTvwiuM%252C_%253B9wap5LETXIiyeM%252CvmACvpHeD8wjzM%252C_%253BUM_eL7wgIA4TzM%252Cht29CcN2-zkCmM%252C_%253BbD0isLX_mMditM%252CUaYZJgG5S8YD_M%252C_%253BBOdzc8KWiDY8NM%252CutJ6P3JCqQQaqM%252C_%253Bpa79vU-KoN22RM%252CvehskR6bZeyrDM%252C_%253BA_TJ5B4BpiRzNM%252CMia68JDKgpGbjM%252C_%253BFLmguN1OEj56cM%252ChVpvvL-HxQGRYM%252C_%253B-26XY9qdulwNzM%252CoJpDlactehw_PM%252C_%253BR9ZXN0PdxNaW6M%252ChVpvvL-HxQGRYM%252C_%253BvvD4WElq4gnsyM%252C3-2brnTfwyHyvM%252C_&vet=1&usg=AI4_-kScUnY2qN3qYG3mY9fDeP6nejkluA&sa=X&ved=2ahUKEwiH_c_KpsjzAhWRpnIEHdgsDj4Q9QF6BAgdEAE#imgrc=_tJ9zISYcJ92GM",
    },
  ];
  return (
    <Grid
      container
      direction="column"
      justifyContent="space-around"
      alignItems="stretch"
    >
      <Grid item xs>
        <Slider {...settings}>
          {employees.map(function (employee) {
            return (
              <div>
                <span>{employee.name}</span>
              </div>
            );
          })}
        </Slider>
      </Grid>
      <div>
        <Grid item xs>
          <TextField
            id="standard-basic"
            label="Team Name"
            variant="standard"
            value={NewTeamName}
            onChange={(e) => {
              setNewTeamName(e.target.value);
            }}
          />
          <Button
            onClick={() => {
              dispatch(AddTeam(teams, NewTeamName));
              setNewTeamName("");
            }}
          >
            Add Team
          </Button>
        </Grid>
        <Grid item xs>
          {t.map((team) => {
            return <Team key={team.id} team={team} />;
          })}
        </Grid>
      </div>
      <div>
        {alliances.map((alliance) => {
          return (
            <Alliance key={alliance.id} alliance={alliance} teams={teams} />
          );
        })}
      </div>
      {/* </div> */}
    </Grid>
  );
}

export default DragDrop;
