import React from "react";
import { useDrop, useDrag } from "react-dnd";
import { addEmployeeToTeam } from "../helpers/functionallity/updateEmployee.js";
import { deleteTeam } from "../actions/teams";
import { useDispatch } from "react-redux";
import Card from "@mui/material/Card";
import { Button } from "@mui/material";
import CardHeader from "@mui/material/CardHeader";

import DeleteTwoToneIcon from "@mui/icons-material/DeleteTwoTone";
import ReactDOM from "react-dom";
import { useState } from "react";
import ReactCardFlip from "react-card-flip";

const TeamInAlliance = (props) => {
  const [isFlipped, setIsFlipped] = useState(false);

  const handleClick = () => {
    setIsFlipped(!isFlipped);
  };
  const dispatch = useDispatch();
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "employee",
    drop: (item) => {
      dispatch(addEmployeeToTeam(item.employee, props.team, props.employees));
    },
  }));
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "team",
    item: { team: props.team },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));
  return (
    <div ref={drop}>
      <ReactCardFlip isFlipped={isFlipped} flipDirection="horizontal">
        <div>
          <Card
            className="teams"
            key={props.team.id}
            ref={drag}
            style={{
              backgroundColor: "rgba(255, 255, 255, 0.3)",
              borderRadius: "20px",
            }}
          >
            <CardHeader title={props.team.Name} />

            <Button
              style={{
                opacity: "0.4",
                margin: "1vh",
              }}
              onClick={() => {
                dispatch(deleteTeam(props.team, props.teams));
              }}
            >
              <DeleteTwoToneIcon
                style={{
                  position: "absolute",
                  bottom: 55,
                  right: -60,
                  color: "red",
                }}
              />
            </Button>
          </Card>

          <button onClick={handleClick}> {">"} </button>
        </div>

        <div
          style={{
            backgroundColor: "red",
            height: 100,
            color: "#fff",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          Create video tutorials on YouTube
          <button onClick={handleClick}> {">"} </button>
        </div>
      </ReactCardFlip>
    </div>
  );
};

export default TeamInAlliance;
