import React, { useEffect } from "react";
import { getEmployees } from "./actions/employees";
import { getToken } from "./actions/token";
import { getTeams } from "./actions/teams";
import { getAlliances } from "./actions/alliances";
import { useDispatch } from "react-redux";
import { HTML5Backend } from "react-dnd-html5-backend";
import { DndProvider } from "react-dnd";
import DragDrop from "./components/DragDrop";
import "./styles/App.css";

function App() {
  const dispatch = useDispatch();

  useEffect(async () => {
    try {
      const token = await dispatch(getToken());
      const employees = await dispatch(getEmployees());
      const teams = await dispatch(getTeams());
      const alliances = await dispatch(getAlliances());
    } catch (err) {
      console.log(err);
    }
  }, [dispatch]);
  return (
    <div id="slider">
      <input type="radio" name="slider" id="slide1" checked></input>
      <input type="radio" name="slider" id="slide2"></input>
      <input type="radio" name="slider" id="slide3"></input>
      <input type="radio" name="slider" id="slide4"></input>
      <div id="slides">
        <div id="overflow">
          <div className="inner">
            <div className="slide slide_1">
              <div className="slide-content">
                <h2>Slide 1</h2>
                <p>Content for Slide 1</p>
              </div>
            </div>
            <div className="slide slide_2">
              <div className="slide-content">
                <h2>Slide 2</h2>
                <p>Content for Slide 2</p>
              </div>
            </div>
            <div className="slide slide_3">
              <div className="slide-content">
                <h2>Slide 3</h2>
                <p>Content for Slide 3</p>
              </div>
            </div>
            <div className="slide slide_4">
              <div className="slide-content">
                <h2>Slide 4</h2>
                <p>Content for Slide 4</p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div id="controls">
        <label htmlFor="slide1"></label>
        <label htmlFor="slide2"></label>
        <label htmlFor="slide3"></label>
        <label htmlFor="slide4"></label>
      </div>
    </div>
  );
}

export default App;
