import * as api from "../helpers/Api/fetchDataApi.js";
import * as functionallity from "../helpers/functionallity/updateEmployee";

import store from "../index";
// action Creators
export const getEmployees = () => async (dispatch) => {
  try {
    const data = await api.fetchEmployees(store.getState().token);
    dispatch({ type: "FETCH_ALL_EMPLOYEES", payload: data });
  } catch (err) {
    console.log(err);
  }
};

export const addEmployeeToTeam =
  (employee, team, employees, teams) => async (dispatch) => {
    try {
      const [employeesUpdated, teamsUpdated] =
        await functionallity.addEmployeeToTeam(
          employee,
          team,
          employees,
          teams
        );
      dispatch({ type: "UPDATE_TEAMS", payload: teamsUpdated });
      // dispatch({ type: "UPDATE_EMPLOYEES", payload: employeesUpdated });
    } catch (err) {
      console.log(err);
    }
  };
