import * as api from "../helpers/Api/fetchDataApi.js";
import store from "../index";
import * as functionallity from "../helpers/functionallity/updateTeams";

// action Creators
export const getAlliances = () => async (dispatch) => {
  try {
    const data = await api.fetchAlliances(store.getState().token);
    dispatch({ type: "FETCH_ALL_ALLIANCES", payload: data });
  } catch (err) {
    console.log(err);
  }
};
export const addTeamToAlliance =
  (team, alliance, teams) => async (dispatch) => {
    console.log("TRY TO ADD TEAM TO ALLIANCE");
    try {
      const teamsUpdated = await functionallity.addTeamToAlliance(
        team,
        alliance,
        teams
      );
      dispatch({ type: "UPDATE_ALLIANCES", payload: teamsUpdated });
    } catch (err) {
      console.log(err);
    }
  };
