import axios from "axios";
import store from "../../index";

const addTeamToAllianceApi = async (team, alliance) => {
  var data = JSON.stringify({
    alliance: alliance.id,
  });
  const team_url = `http://cms.avertra.com/teams/${team.id}`;

  var config = {
    method: "put",
    url: team_url,
    headers: {
      Authorization: "Bearer " + store.getState().token,
      "Content-Type": "application/json",
    },
    data: data,
  };

  axios(config)
    .then(function (response) {
      console.log("team added to alliance");
    })
    .catch(function (error) {
      console.log(error);
    });
};

const getTeamsBelongTothisAlliance = async (alliance) => {
  var config = {
    method: "get",
    url: "http://cms.avertra.com/teams?alliance.Name=${alliance",
    headers: {
      Authorization:
        "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxNTJiNmU1OTExZjZiYzY3MmQzZDZmNSIsImlhdCI6MTYzMjgzOTE1MCwiZXhwIjoxNjM1NDMxMTUwfQ.XEAwacR7yXCLA01xqXdnxTYuGWbEXGaICpUwrg2uZcE",
    },
  };

  axios(config)
    .then(function (response) {
      console.log(JSON.stringify(response.data));
    })
    .catch(function (error) {
      console.log(error);
    });
};
export { addTeamToAllianceApi };
