import * as helaperApi from "../Api/helperApis.js";

export const addNewTeam = async (NewTeamName) => {
  try {
    const data = await helaperApi.createNewTeam(NewTeamName);
    return data;
  } catch (err) {
    console.log(err);
  }
};
export const addTeamToAlliance = async (team, alliance) => {
  console.log("add Team To Alliance Functionallity ");
  try {
    api.addTeamToAllianceApi(team, alliance);
  } catch (err) {
    console.log(err);
  }
};

export const deleteTeam = async (team, teams) => {
  try {
    await helaperApi.deleteTeam(team);
    const data = teams.filter((t) => {
      return t.id !== team.id;
    });
    return data;
  } catch (err) {
    console.log(err);
  }
};
