export function addEmployeeToTeam(employee, team) {
  var team = JSON.stringify(team);
  localStorage.setItem(
    "currentTeam",
    localStorage.getItem(`${props.alliance.id}`).concat(team)
  );
  console.log(`${employee["Name"]} added to the team`);
}
