import * as api from "../helpers/Api/fetchDataApi.js";
import store from "../index";
// action Creators
export const getTeams = () => async (dispatch) => {
  try {
    const data = await api.fetchTeams(store.getState().token);
    dispatch({ type: "FETCH_ALL_TEAMS", payload: data });
  } catch (err) {
    console.log(err);
  }
};
export const updateTeams = (team) => async (dispatch) => {
  try {
    const data = await api.fetchTeams(store.getState().token);
     data.concat(team)
    dispatch({ type: "UPDATE_TEAM", payload: data.concat(team) });
  } catch (err) {
    console.log(err);
  }
};
