import React, { useState, useEffect } from "react";
import Employee from "./Employee";
import Team from "./Team";
import Alliance from "./Alliance";
import { useSelector, useDispatch } from "react-redux";
import TextField from "@material-ui/core/TextField";
import { Button } from "@mui/material";
import { AddTeam } from "../actions/teams";
import Grid from "@mui/material/Grid";
import "../styles/App.css";
import Slider from "react-slick";
import { Container, Card, Row, Col } from "react-bootstrap";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Add from "@material-ui/icons/Add";
import { Badge, IconButton, Typography } from "@material-ui/core";

function DragDrop() {
  const dispatch = useDispatch();
  const teams = useSelector((state) => {
    return state.teams;
  });
  var t = teams.filter((t) => {
    return t.alliance === undefined;
  });
  var settings = {
    dots: true,
    infinite: false,
    speed: 200,
    slidesToShow: 4,
    slidesToScroll: 1,
  };
  console.log(teams, "TEAMS Drag and DROP");
  console.log(t, "t Drag and DROP");
  const [searchField, setsearchField] = useState("");
  const [NewTeamName, setNewTeamName] = useState("");
  const employees = useSelector((state) => state.employees);
  const alliances = useSelector((state) => state.alliances);
  var filteredEmployees = employees.filter((emp) =>
    emp["Name"].toLowerCase().includes(searchField.toLowerCase())
  );
  filteredEmployees = filteredEmployees
    .concat(filteredEmployees)
    .concat(filteredEmployees);
  return (
    <Grid
      container
      direction="column"
      justifyContent="space-around"
      alignItems="stretch"
    >
      <Grid item xs>
        <TextField
          fullWidth
          id="standard-basic"
          label="Search Employee"
          variant="standard"
          onChange={(e) => setsearchField(e.target.value)}
        />
        <Container className="employeesContainer">
          <Slider className="employees" {...settings}>
            {filteredEmployees.map(function (employee) {
              return (
                <React.Fragment>
                  <Col style={{ display: "flex", flexDirection: "row" }} s>
                    <Employee employee={employee} />
                  </Col>
                </React.Fragment>
              );
            })}
          </Slider>
        </Container>
      </Grid>
      <div>
        <Grid item xs>
          <TextField
            fullWidth
            id="standard-basic"
            label="Team Name"
            variant="standard"
            value={NewTeamName}
            onChange={(e) => {
              setNewTeamName(e.target.value);
            }}
          />
          <Button
            variant={"contained"}
            fullWidth
            style={{
              height: "30px",
              opacity: "0.4",
              margin: "1vh",
            }}
            onClick={() => {
              dispatch(AddTeam(teams, NewTeamName));
              setNewTeamName("");
            }}
          >
            <Typography
              variant={"button"}
              style={{ fontSize: 19, paddingRight: 5 }}
            >
              Add Team
            </Typography>
            <Add style={{ position: "absolute", bottom: 5, right: 50 }} />
          </Button>
        </Grid>
        <Container className="employeesContainer">
          <Slider className="employees" {...settings}>
            {t.map((team) => {
              return (
                <React.Fragment>
                  <Col style={{ display: "flex", flexDirection: "row" }} s>
                    <Team key={team.id} team={team} />
                  </Col>
                </React.Fragment>
              );
            })}
          </Slider>
        </Container>
      </div>
      <div>
        {alliances.map((alliance) => {
          return (
            <Alliance key={alliance.id} alliance={alliance} teams={teams} />
          );
        })}
      </div>
      {/* </div> */}
    </Grid>
  );
}

export default DragDrop;
