import axios from "axios";

const getToken = async (setToken) => {
  try {
    const resp = await axios.post("http://cms.avertra.com/auth/local", {
      identifier: "Cms@avertra.com",
      password: "Avertra@2021",
    });
    return resp.data.jwt;
  } catch (err) {
    console.error(err);
  }
};

const fetchEmployees = async (token) => {
  const emp_url = "http://cms.avertra.com/employees";
  const headers = { Authorization: "Bearer " + token };
  try {
    const resp = await axios.get(emp_url, {
      headers: headers,
    });

    console.log(resp.data, "111111111111111111111");
    return resp.data;
  } catch (err) {
    console.error(err);
    return [];
  }
};

export { getToken, fetchEmployees };
