    accept: "employee",
    drop: (item) => addEmployeeToTeam(item),
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  }));

  const addEmployeeToTeam = (item) => {
    console.log(item, "teams");
  };
  return (
    <div className="team" key={team.id} ref={drop}>
      <div> {team.Name} </div>
    </div>
  );
}

export default Team;
