import React, { useEffect, useState } from "react";
import { useDrop, useDrag } from "react-dnd";
import { addTeamToAlliance } from "../actions/teams";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import store from "../index";
import { getTeamBelongToThisAlliance } from "../helpers/Api/helperApis.js";
import { useDispatch } from "react-redux";
import CardHeader from "@mui/material/CardHeader";
import Card from "@mui/material/Card";

import TeamInAlliance from "./TeamInAlliance";
function Alliance(props) {
  const dispatch = useDispatch();
  const [teams, setTeams] = useState(props.teams);

  useEffect(async () => {
    try {
      setTeams(await getTeamBelongToThisAlliance(props.alliance));
    } catch (err) {
      console.log(err);
    }
  }, [teams]);

  const [{ isOver }, drop] = useDrop(() => ({
    accept: "team",
    drop: (item) => {
      dispatch(addTeamToAlliance(item.team, props.alliance, teams));
    },
  }));

  return (
    <Card
      ref={drop}
      style={{
        backgroundColor: "rgba(255, 255, 255, 0.3)",
        borderRadius: "20px",
        display: "flex",
      }}
    >
      {props.alliance.Name}

      {teams.map((team) => {
        console.log(props.alliance.Name, "NAmeeeeeeeeeeeeeeee");
        return <TeamInAlliance key={team.id} team={team} teams={teams} />;
      })}
    </Card>
  );
}

export default Alliance;
