import React, { useEffect, useState } from "react";
import { useDrop, useDrag } from "react-dnd";
import { addTeamToAlliance } from "../actions/teams";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import { getTeamBelongToThisAlliance } from "../helpers/Api/helperApis.js";
import { useDispatch } from "react-redux";
import Team from "./Team";
function Alliance(props) {
  const dispatch = useDispatch();
  const [teams, setTeams] = useState([]);

  useEffect(async () => {
    try {
      setTeams(await getTeamBelongToThisAlliance(props.alliance));
    } catch (err) {
      console.log(err);
    }
  }, [teams]);

  const [{ isOver }, drop] = useDrop(() => ({
    accept: "team",
    drop: (item) => {
      dispatch(addTeamToAlliance(item.team, props.alliance, props.teams));
    },
  }));
  const [{ isDragging }, innerdrag] = useDrag(() => ({
    type: "innerTeam",
    item: { alliance: props.alliance },
    drag: console.log("dragging"),
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  return (
    <List
      className="alliance"
      ref={drop}
      sx={{
        width: "100%",
        maxWidth: 360,
        bgcolor: "background.paper",
        marginLeft: "50px",
        marginBottom: "50px",
      }}
    >
      {teams.map((team) => {
        return (
          <ListItem alignItems="flex" key={props.alliance.id.concat(team.id)}>
            <Team key={team.id} team={team} teams={teams} />
          </ListItem>
        );
      })}
    </List>
  );
}

export default Alliance;
