import React, { useEffect } from "react";
import { useDrag } from "react-dnd";
import Avatar from "@mui/material/Avatar";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";

function Employee(props) {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "employee",
    item: { employee: props.employee, employees: props.employees },

    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));
  const style = {
    backgroundColor: "rgba(255, 255, 255, 0.3)",
    borderRadius: "20px",
  };
  useEffect(async () => {
    await document.get.slick({
      autoplay: true, //自動的に動き出すか。初期値はfalse。
      infinite: true, //スライドをループさせるかどうか。初期値はtrue。
      slidesToShow: 3, //スライドを画面に3枚見せる
      slidesToScroll: 3, //1回のスクロールで3枚の写真を移動して見せる
      prevArrow: '<div class="slick-prev"></div>', //矢印部分PreviewのHTMLを変更
      nextArrow: '<div class="slick-next"></div>', //矢印部分NextのHTMLを変更
      dots: true, //下部ドットナビゲーションの表示
      responsive: [
        {
          breakpoint: 769, //モニターの横幅が769px以下の見せ方
          settings: {
            slidesToShow: 2, //スライドを画面に2枚見せる
            slidesToScroll: 2, //1回のスクロールで2枚の写真を移動して見せる
          },
        },
        {
          breakpoint: 426, //モニターの横幅が426px以下の見せ方
          settings: {
            slidesToShow: 1, //スライドを画面に1枚見せる
            slidesToScroll: 1, //1回のスクロールで1枚の写真を移動して見せる
          },
        },
      ],
    });
  });

  return (
    <div>
      <h2>複数画像を並列に見せる</h2>

      <ul class="slider">
        <li>
          <img
            src="https://coco-factory.jp/ugokuweb/wp-content/themes/ugokuweb/data/6-1-6/img/img_01.jpg"
            alt=""
          ></img>
        </li>
        <li>
          <img
            src="https://coco-factory.jp/ugokuweb/wp-content/themes/ugokuweb/data/6-1-6/img/img_02.jpg"
            alt=""
          ></img>
        </li>
        <li>
          <img
            src="https://coco-factory.jp/ugokuweb/wp-content/themes/ugokuweb/data/6-1-6/img/img_03.jpg"
            alt=""
          ></img>
        </li>
        <li>
          <img
            src="https://coco-factory.jp/ugokuweb/wp-content/themes/ugokuweb/data/6-1-6/img/img_04.jpg"
            alt=""
          ></img>
        </li>
        <li>
          <img
            src="https://coco-factory.jp/ugokuweb/wp-content/themes/ugokuweb/data/6-1-6/img/img_05.jpg"
            alt=""
          ></img>
        </li>
        <li>
          <img
            src="https://coco-factory.jp/ugokuweb/wp-content/themes/ugokuweb/data/6-1-6/img/img_06.jpg"
            alt=""
          ></img>
        </li>
      </ul>
      <p>
        使用したライブラリ：
        <a href="https://kenwheeler.github.io/slick/" target="_blank">
          https://kenwheeler.github.io/slick/
        </a>
      </p>

      <script
        src="https://code.jquery.com/jquery-3.4.1.min.js"
        integrity="sha256-CSXorXvZcTkaix6Yvo6HppcZGetbYMGWSFlBw8HfCJo="
        crossorigin="anonymous"
      ></script>
      <script src="https://cdn.jsdelivr.net/npm/slick-carousel@1.8.1/slick/slick.min.js"></script>
      <script src="https://coco-factory.jp/ugokuweb/wp-content/themes/ugokuweb/data/6-1-6/js/6-1-6.js"></script>
    </div>
  );
}

export default Employee;
