import React, { useState, useEffect } from "react";
import { DropTarget, DragSource } from "react-dnd";
const itemTarget = {
  drop(props) {
    var team = JSON.stringify(props.team);
    localStorage.setItem("currentTeam", team);
  },
};
const itemSource = {
  beginDrag(props) {
    return props.team;
  },
  endDrag(props, monitor) {
    if (!monitor.didDrop()) {
      return;
    }
    return props.handleDrop2(props.team);
  },
};
function collectTarget(connect, monitor) {
  return {
    connectDropTarget: connect.dropTarget(),
    item: monitor.getItem(),
  };
}
function collectSource(connect, monitor) {
  return {
    connectDragSource: connect.dragSource(),
    isDragging: monitor.isDragging(),
  };
}

function Team() {
   const { hovered, connectDropTarget, connectDragSource, team } = this.props;
   const bkcolor = hovered ? "lightgreen" : "white";
  return <div className="App">team</div>;
}
let MyComponent = DropTarget("item", itemTarget, collectTarget)(Team);
MyComponent = DragSource("MyComponent", itemSource, collectSource)(MyComponent);

export default MyComponent;
