import React, { useState } from "react";
import ReactCardFlip from "react-card-flip";
import { useDrop, useDrag } from "react-dnd";
import { addEmployeeToTeam } from "../actions/teams";
import { deleteTeam } from "../actions/teams";
import { useDispatch } from "react-redux";
import Card from "@mui/material/Card";
import { Button } from "@mui/material";
import CardHeader from "@mui/material/CardHeader";
import DeleteTwoToneIcon from "@mui/icons-material/DeleteTwoTone";
import Avatar from "@mui/material/Avatar";
import { connect } from "react-redux";

function Team(props) {
  const [isFlipped, setIsFlipped] = useState(false);

  const dispatch = useDispatch();
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "employee",
    drop: (item) => {
      dispatch(
        addEmployeeToTeam(
          item.employee,
          props.team,
          props.employees,
          props.teams
        )
      );
    },
  }));
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "team",
    item: { team: props.team },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));
console.log(props.team,)
  return (
    <div ref={drop}>
      <div ref={drag} className="team">
        <div>
          <Card
            style={{
              marginBottom: "20px",
              backgroundColor: "rgba(255, 255, 255, 0.3)",
              borderRadius: "20px",
              marginRight: "10px",
              display: "flex",
              flexDirection: "row",
            }}
          >
            <CardHeader title={props.team.Name} />
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                flexWrap: "wrap",
              }}
            >
              {props.team.employees.length === 0
                ? "No Employees Here"
                : props.team.employees.map((e) => {
                    return (
                      <div key={e.id}>
                        <CardHeader
                          style={{ display: "flex" }}
                          avatar={<Avatar src={e["Avatar"]["url"]} />}
                        />
                      </div>
                    );
                  })}
            </div>
            <Button
              style={{
                position: "absolute",
                bottom: 0,
                right: 2,
                opacity: 0.3,
                color: "red",
              }}
              onClick={() => {
                dispatch(deleteTeam(props.team, props.teams));
              }}
            >
              <DeleteTwoToneIcon
                style={{
                  color: "red",
                }}
              />
            </Button>
          </Card>
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = (state, ownProps) => {
  return {
    state: state,
    props: ownProps,
  };
};

export default connect(mapStateToProps)(Team);
