export function addEmployeeToTeam(employee, team) {
  var team = JSON.stringify(team);
  localStorage.setItem("currentTeam", team);
}
import * as helaperApi from "../Api/helperApis.js";

export const addNewTeam = async (NewTeamName) => {
  try {
    const data = await helaperApi.createNewTeam(NewTeamName);
    return data;
  } catch (err) {
    console.log(err);
  }
};

export const deleteTeam = async (team, teams) => {
  try {
    await helaperApi.deleteTeam(team);
    const data = teams.filter((t) => {
      return t.id !== team.id;
    });
    return data;
  } catch (err) {
    console.log(err);
  }
};
export const addTeamToAlliance = async (team, alliance, teams) => {
  try {
    const teamUpdated = await helaperApi.addTeamToAllianceApi(team, alliance);
    var UpdatedTeams = teams.filter((t) => {
      return t.id !== team.id;
    });
    console.log(teamUpdated, "TEAM UPDAETEDDDD ");
    console.log(UpdatedTeams, "TEAMSSSSSSSSSSSSS UPDATED 11111111111111");

    UpdatedTeams = UpdatedTeams.concat(teamUpdated);
    console.log(
      UpdatedTeams,
      "TEAMSSSSSSSSSSSSS UPDATED 222222222222222222222222222222222"
    );

    return UpdatedTeams;
  } catch (err) {
    console.log(err);
  }
};
