import React, { useState, useEffect } from "react";
import Employee from "./Employee";
import Team from "./Team";
import Alliance from "./Alliance";
import { useSelector, useDispatch } from "react-redux";
import TextField from "@material-ui/core/TextField";
import { Button } from "@mui/material";
import { AddTeam } from "../actions/teams";
import Grid from "@mui/material/Grid";
import "../styles/App.css";
import Slider from "react-slick";
import { Container, Card, Row, Col } from "react-bootstrap";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
function DragDrop() {
  const dispatch = useDispatch();
  const teams = useSelector((state) => {
    return state.teams;
  });
  var t = teams.filter((t) => {
    return t.alliance === undefined;
  });
  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };
  console.log(teams, "TEAMS Drag and DROP");
  console.log(t, "t Drag and DROP");
  const [searchField, setsearchField] = useState("");
  const [NewTeamName, setNewTeamName] = useState("");
  const employees = useSelector((state) => state.employees);
  const alliances = useSelector((state) => state.alliances);
  const filteredEmployees = employees.filter((emp) =>
    emp["Name"].toLowerCase().includes(searchField.toLowerCase())
  );
  const movies = employees;
  return (
    <Container>
      <div className="clearfix mt-5 mb-2">
        <h4 className="float-left">Now Playing</h4>
      </div>
      <Slider {...settings}>
        {movies.map(function (movie) {
          return (
            <React.Fragment>
              <Col>
                <Card>
                  <Card.Img variant="top" src={` ${movie.Avatar.ur}`} />
                  <Card.Body>
                    <span>{movie.title}</span>
                  </Card.Body>
                </Card>
              </Col>
            </React.Fragment>
          );
        })}
      </Slider>
    </Container>
  );
}

export default DragDrop;
