import React, { useState, useEffect } from "react";
import Employee from "./Employee";
import Team from "./Team";
import Alliance from "./Alliance";
import { useSelector, useDispatch } from "react-redux";
import TextField from "@material-ui/core/TextField";
import { Button } from "@mui/material";
import { AddTeam } from "../actions/teams";
import Grid from "@mui/material/Grid";
import Add from "@material-ui/icons/Add";
import { Typography } from "@material-ui/core";
import Slider from "react-slick";
import { Container, Col } from "react-bootstrap";
import "../styles/App.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "bootstrap/dist/css/bootstrap.min.css";

function DragDrop() {
  const dispatch = useDispatch();
  const teams = useSelector((state) => {
    return state.teams.filter((t) => {
      return t.alliance === undefined;
    });
  });

  const settings = {
    dots: true,
    infinite: false,
    speed: 200,
    slidesToShow: 4,
    slidesToScroll: 1,
  };
  console.log(teams, "TEAMS Drag and DROP");
  const [searchField, setsearchField] = useState("");
  const [NewTeamName, setNewTeamName] = useState("");
  const employees = useSelector((state) => state.employees);
  const alliances = useSelector((state) => state.alliances);
  const filteredEmployees = employees.filter((emp) =>
    emp["Name"].toLowerCase().includes(searchField.toLowerCase())
  );

  return (
    <Grid
      container
      direction="column"
      justifyContent="space-around"
      alignItems="stretch"
    >
      <Grid item xs>
        <TextField
          fullWidth
          id="standard-basic"
          label="Search Employee"
          variant="standard"
          onChange={(e) => setsearchField(e.target.value)}
        />
        <Container className="container">
          <Slider className="slider" {...settings}>
            {filteredEmployees.map(function (employee) {
              return (
                <React.Fragment>
                  <Col style={{ display: "flex", flexDirection: "row" }} s>
                    <Employee employee={employee} />
                  </Col>
                </React.Fragment>
              );
            })}
          </Slider>
        </Container>
      </Grid>
      <div>
        <Grid item xs>
          <TextField
            fullWidth
            id="standard-basic"
            label="Team Name"
            variant="standard"
            value={NewTeamName}
            onChange={(e) => {
              setNewTeamName(e.target.value);
            }}
          />
          <Button
            variant={"contained"}
            fullWidth
            style={{
              height: "30px",
              opacity: "0.4",
              margin: "1vh",
            }}
            disabled={NewTeamName.length === 0}
            onClick={() => {
              dispatch(AddTeam(teams, NewTeamName));
              setNewTeamName("");
            }}
          >
            <Typography
              variant={"button"}
              style={{ fontSize: 19, paddingRight: 5 }}
            >
              Add Team
            </Typography>
            <Add style={{ position: "absolute", bottom: 4, right: 870 }} />
          </Button>
        </Grid>
        <Container className="container">
          <Slider className="slider" {...settings}>
            {teams.map((team) => {
              const teamEmployees = employees.filter((e) => {
                return e.teams.filter((t) => {
                  return t.id === team.id;
                });
              });

              return (
                <Col style={{ display: "flex", flexDirection: "row" }} s>
                  <Team
                    key={team.id}
                    team={team}
                    employees={employees}
                    teamEmployees={teamEmployees}
                  />
                </Col>
              );
            })}
          </Slider>
        </Container>
      </div>

      <Container className="container">
        <Slider className="slider" {...settings}>
          {alliances.map((alliance) => {
            return (
              <Col style={{ display: "flex", flexDirection: "row" }}>
                <Alliance key={alliance.id} alliance={alliance} teams={teams} />
              </Col>
            );
          })}
        </Slider>
      </Container>
    </Grid>
  );
}

export default DragDrop;
