import React from "react";
import { useDrop } from "react-dnd";
import { addTeamToAlliance } from "../helpers/functionallity/teamAlliance";
import { getTeamsBelongTothisAlliance } from "../helpers/Api/helperApis";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
function Alliance(props) {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "team",
    drop: (item) => {
      addTeamToAlliance(item.team, props.alliance);
    },
  }));
  let teams = getTeamsBelongTothisAlliance(props.alliance);
  return (
    <List className="alliance" ref={drop}>
      <ListItem>
        <ListItemAvatar>
          <Avatar>{props.alliance.Name[0]}</Avatar>
        </ListItemAvatar>
        <ListItemText primary={props.alliance.Name} />
        <div>
          {teams.map((team) => {
            return <Team key={team.id} team={team} />;
          })}
        </div>
        {/* nested list */}
        <List>
          <ListItem>
            <ListItemAvatar>
              <Avatar>{props.alliance.Name[0]}</Avatar>
            </ListItemAvatar>
            <ListItemText primary={props.alliance.Name} />
          </ListItem>
        </List>
        {/* nested list */}
      </ListItem>
    </List>
  );
}
export default Alliance;
