import React, { useEffect, useState } from "react";
import { useDrop, useDrag } from "react-dnd";
import { addTeamToAlliance } from "../actions/teams";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import { getTeamBelongToThisAlliance } from "../helpers/Api/helperApis.js";
import { useDispatch } from "react-redux";

function Alliance(props) {
  const dispatch = useDispatch();
  const [teams, setTeams] = useState([]);
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "team",
    drop: (item) => {
      dispatch(addTeamToAlliance(item.team, props.alliance, props.teams));
    },
  }));
  const [innerdrag] = useDrag(() => ({
    type: "innerTeam",
    drag: () => {
      console.log("Dragging");
    },
  }));
  const [innerdrop] = useDrop(() => ({
    accept: "innerTeam",
    drop: (item) => {
      console.log("Dropping");
    },
  }));
  useEffect(async () => {
    try {
      setTeams(await getTeamBelongToThisAlliance(props.alliance));
    } catch (err) {
      console.log(err);
    }
  }, [teams]);
  return (
    <List
      className="alliance"
      ref={drop}
      ref={innerdrop}
      sx={{
        width: "100%",
        maxWidth: 360,
        bgcolor: "background.paper",
        marginLeft: "50px",
        marginBottom: "50px",
      }}
    >
      {teams.map((team) => {
        return (
          <ListItem
            alignItems="flex-start"
            key={props.alliance.id.concat(team.id)}
          >
            <ListItemAvatar>
              <Avatar
                ref={innerdrag}
                alt={team.Name[0]}
                src="/static/images/avatar/1.jpg"
              />
            </ListItemAvatar>
            <ListItemText primary={team.Name} />
          </ListItem>
        );
      })}
    </List>
  );
}

export default Alliance;
