import * as api from "../helpers/Api/fetchDataApi.js";
import store from "../index";
// action Creators
export const getEmployees = () => async (dispatch) => {
  try {
    const data = await api.fetchEmployees(store.getState().token);
    dispatch({ type: "FETCH_ALL_EMPLOYEES", payload: data });
  } catch (err) {
    console.log(err);
  }
};


export const addEmployeeToTeam = (team, alliance, teams) => async (dispatch) => {
  console.log(team, "team in action ");
  console.log(alliance, "alliance  in action ");
  console.log(teams, "teams  in action ");
  try {
    const teamsUpdated = await functionallity.addTeamToAlliance(
      team,
      alliance,
      teams
    );
    console.log(teamsUpdated, "teams Updateddddddddd d d  d d d d d  d d ");
    dispatch({ type: "UPDATE_TEAMS", payload: teamsUpdated });
  } catch (err) {
    console.log(err);
  }
};
