import * as helaperApi from "../Api/helperApis.js";

export const addTeamToAlliance = async (team, alliance, teams, alliances) => {
  //     var employeesUpdated = team.employees.filter((e) => e.id !== employee.id);
  //     employeesUpdated = employeesUpdated.concat(employeeUpdated);
  //     var teamsUpdated = teams.filter((t) => t.id !== team.id);
  //     const index = teams.findIndex((x) => x.id === team.id);
  //     team.employees = employeesUpdated;
  //     teamsUpdated.splice(index, 0, team);
  //     return teamsUpdated;
  try {
    const teamUpdated = await helaperApi.addTeamToAllianceApi(team, alliance);
    var teamsUpdated = alliance.teams.filter((t) => t.id !== team.id);
    teamsUpdated = teamsUpdated.concat(teamUpdated);
    var allianceUpdated = alliances.filter((t) => t.id !== alliance.id);

    var UpdatedTeams = teams.filter((t) => {
      return t.id !== team.id;
    });

    UpdatedTeams = UpdatedTeams.concat(teamUpdated);

    return UpdatedTeams;
  } catch (err) {
    console.log(err);
  }
};
