import * as helaperApi from "../Api/helperApis.js";

export const addEmployeeToTeam = async (employee, team) => {
  try {
    const employeeUpdated = await helaperApi.addEmployeeToTeam(employee, team);
    var employeesUpdated = [].filter((e) => e.id !== employee.id);
    employeesUpdated.concat(employeeUpdated);
    return employeesUpdated;
  } catch (err) {
    console.log(err);
  }
};
