import React from "react";
import { useDrop } from "react-dnd";
import { addEmployeeToTeam } from "../helpers/functionallity.js/employeeTeam";
function Team(props) {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "employee",

    drop: (e) => {
      var team = JSON.stringify(props.team);
      localStorage.setItem("currentTeam", team);
    },
  }));
  return (
    <div className="teams" key={props.team.id} ref={drop}>
      <div> {props.team.Name} </div>
    </div>
  );
}

export default Team;
