import React, { useEffect, useState } from "react";
import { useDrop, useDrag } from "react-dnd";
import { addTeamToAlliance } from "../actions/teams";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import store from "../index";
import { Container, Col } from "react-bootstrap";

import { getTeamBelongToThisAlliance } from "../helpers/Api/helperApis.js";
import { useDispatch } from "react-redux";
import TeamInAlliance from "./TeamInAlliance";
function Alliance(props) {
  const dispatch = useDispatch();
  const [teams, setTeams] = useState(props.teams);

  useEffect(async () => {
    try {
      setTeams(await getTeamBelongToThisAlliance(props.alliance));
    } catch (err) {
      console.log(err);
    }
  }, [teams]);

  const [{ isOver }, drop] = useDrop(() => ({
    accept: "team",
    drop: (item) => {
      dispatch(addTeamToAlliance(item.team, props.alliance, teams));
    },
  }));

  return (
    <List
      className="alliance"
      ref={drop}
      sx={{
        width: "100%",
        maxWidth: 360,
        display: "flex",
        marginLeft: "50px",
        marginBottom: "50px",
      }}
    >
      {teams.map((team) => {
        return (
          <ListItem
            
          >
         
          </ListItem>
        );
      })}
    </List>
  );
}

export default Alliance;
