import * as api from "../helpers/Api";

// action Creators
export const getEmployees = () => async (dispatch) => {
  try {
    const token = useSelector((state) => state.token);

    const data = await api.fetchEmployees(token);
    dispatch({ type: "FETCH_ALL", payload: data });
  } catch (err) {
    console.log(err);
  }
};
