export default function Alliance(props) {
  console.log(props.teams, "teamss");
  const dispatch = useDispatch();
  const [teams, setTeams] = useState(props.teams);

  useEffect(async () => {
    try {
      setTeams(await getTeamBelongToThisAlliance(props.alliance));
    } catch (err) {
      console.log(err);
    }
  }, [teams]);

  const [{ isOver }, drop] = useDrop(() => ({
    accept: "team",
    drop: (item) => {
      dispatch(addTeamToAlliance(item.team, props.alliance, teams));
    },
  }));
  const columns = [{ field: "Name", headerName: " ", width: 200 }];

  return (
    <div className="alliances">
