import React, { useEffect, useState } from "react";
import { useDrop, useDrag } from "react-dnd";
import { addTeamToAlliance } from "../actions/teams";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import store from "../index";
import { getTeamBelongToThisAlliance } from "../helpers/Api/helperApis.js";
import { useSelector, useDispatch } from "react-redux";
import Team from "./Team";

function Alliance(props) {
  const dispatch = useDispatch();
  const teams = useSelector((state) => {
    return state.teams;
  });
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "team",
    drop: (item) => {
      console.log(teams, "teams in alliance   compnennt");
      dispatch(addTeamToAlliance(item.team, props.alliance, teams));
    },
  }));

  useEffect(async () => {
    try {
      dispatch(getTeamBelongToThisAlliance(props.alliance));
    } catch (err) {
      console.log(err);
    }
  }, [teams]);

  return (
    <List
      className="alliance"
      ref={drop}
      sx={{
        width: "100%",
        maxWidth: 360,
        bgcolor: "background.paper",
        marginLeft: "50px",
        marginBottom: "50px",
      }}
    >
      {teams
        .filter((t) => t.alliance.id === props.alliance.id)
        .map((team) => {
          return (
            <ListItem
              alignItems="flex-start"
              key={props.alliance.id.concat(team.id)}
            >
              <Team key={team.id} team={team} teams={teams} />
            </ListItem>
          );
        })}
    </List>
  );
}

export default Alliance;
