import React from "react";
import { useDrop, useDrag } from "react-dnd";
import { addEmployeeToTeam } from "../helpers/functionallity/updateEmployee.js";
import { deleteTeamfromalliance } from "../actions/alliances";
import Card from "@mui/material/Card";
import { Button } from "@mui/material";
import CardHeader from "@mui/material/CardHeader";
import Avatar from "@mui/material/Avatar";
import { useSelector, useDispatch } from "react-redux";

import DeleteTwoToneIcon from "@mui/icons-material/DeleteTwoTone";
import { useState } from "react";
import ReactCardFlip from "react-card-flip";

const TeamInAlliance = (props) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "employee",
    drop: (item) => {
      dispatch(
        addEmployeeToTeam(
          item.employee,
          props.team,
          props.employees,
          props.teams
        )
      );
    },
  }));
  const [isFlipped, setIsFlipped] = useState(false);

  const handleClick = () => {
    setIsFlipped(!isFlipped);
  };
  const dispatch = useDispatch();

  const [{ isDragging }, drag] = useDrag(() => ({
    type: "team",
    item: { team: props.team },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));
  return (
    <div ref={drop}>
      <div
        style={{
          height: "20%",
          marginBottom: "10px",
          width: "100%",
        }}
      >
        <ReactCardFlip isFlipped={isFlipped} flipDirection="horizontal">
          <div>
            <Card
              key={props.team.id}
              ref={drag}
              style={{
                backgroundColor: "rgba(255, 255, 255, 0.3)",
                borderRadius: "20px",
              }}
              onClick={handleClick}
            >
              <CardHeader title={props.team.Name} />
              <div>
                <div>
                  <div
                    style={{
                      opacity: "0.4",
                      margin: "1vh",
                    }}
                  >
                    <DeleteTwoToneIcon
                      style={{
                        position: "absolute",
                        bottom: 10,
                        right: 10,
                        color: "blue",
                      }}
                      onClick={() => {
                        dispatch(
                          deleteTeamfromalliance(
                            props.team,
                            props.teams,
                            props.alliance
                          )
                        );
                      }}
                    />
                  </div>
                </div>
              </div>
            </Card>
          </div>

          <Card
            style={{
              display: "inline",
            }}
            onClick={handleClick}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                flexWrap: "wrap",
              }}
            >
              {props.team.employees.length === 0
                ? "No Employees Here"
                : props.team.employees.map((e) => {
                    return (
                      <div key={e}>
                        <CardHeader
                          style={{ display: "flex" }}
                          avatar={
                            <Avatar
                              src={
                                props.employees.find((x) => x.id === e)[
                                  "Avatar"
                                ]["url"]
                              }
                            />
                          }
                        />
                      </div>
                    );
                  })}
            </div>
          </Card>
        </ReactCardFlip>
      </div>
    </div>
  );
};

export default TeamInAlliance;
