import axios from "axios";
import store from "../../index";
// action Creators
const update = (team) => async (dispatch) => {
  try {
    dispatch({ type: "FETCH_ALL_TEAMS", payload: team });
  } catch (err) {
    console.log(err);
  }
};

const addTeamToAllianceApi = async (team, alliance) => {
  var data = JSON.stringify({
    alliance: alliance.id,
  });
  const team_url = `http://cms.avertra.com/teams/${team.id}`;

  var config = {
    method: "put",
    url: team_url,
    headers: {
      Authorization: "Bearer " + store.getState().token,
      "Content-Type": "application/json",
    },
    data: data,
  };

  axios(config)
    .then(function (response) {
      console.log("team added to alliance");
    })
    .catch(function (error) {
      console.log(error);
    });
};

const getTeamBelongToThisAlliance = async (alliance) => {
  var config = {
    method: "get",
    url: `http://cms.avertra.com/teams?alliance.id=${alliance.id}`,
    headers: {
      Authorization: "Bearer " + store.getState().token,
    },
  };
  axios(config)
    .then(function (response) {
      var teams = JSON.stringify(response.data);

      localStorage.setItem(`${alliance.id}`, teams);

      return response.data;
    })
    .catch(function (error) {
      console.log(error);
      return [];
    });
};
const createNewTeam = async (NewTeamName) => {
  var data = JSON.stringify({
    Name: NewTeamName,
  });
  var config = {
    method: "post",
    url: "http://cms.avertra.com/teams",
    headers: {
      Authorization: "Bearer " + store.getState().token,
      "Content-Type": "application/json",
    },
    data: data,
  };

  axios(config)
    .then(function (response) {
      // action Creators
      update(response.data);
    })
    .catch(function (error) {
      console.log(error);
    });
};
const deleteTeam = async (team_id) => {
  var config = {
    method: "delete",
    url: `http://cms.avertra.com/teams/${team_id}`,
    headers: {
      Authorization: "Bearer " + store.getState().token,
    },
  };
  axios(config)
    .then(function (response) {
      console.log("Done");
    })
    .catch(function (error) {
      console.log(error);
    });
};

export {
  addTeamToAllianceApi,
  getTeamBelongToThisAlliance,
  createNewTeam,
  deleteTeam,
};
