import * as fetchApi from "../helpers/Api/fetchDataApi.js";
import * as functionallity from "../helpers/functionallity/addNewTeam";

import store from "../index";
// action Creators
export const getTeams = () => async (dispatch) => {
  try {
    const data = await fetchApi.fetchTeams(store.getState().token);
    dispatch({ type: "FETCH_ALL_TEAMS", payload: data });
  } catch (err) {
    console.log(err);
  }
};
export const AddTeam = (teams, NewTeamName) => async (dispatch) => {
  try {
    var data = JSON.stringify({
      Name: NewTeamName,
    });
    var config = {
      method: "post",
      url: "http://cms.avertra.com/teams",
      headers: {
        Authorization: "Bearer " + store.getState().token,
        "Content-Type": "application/json",
      },
      data: data,
    };
    await axios(config)
      .then((response) => {
        dispatch({
          type: "UPDATE_TEAMS",
          payload: teams.concat(response.data),
        });
        return response.data;
      })
      .catch((error) => {
        console.log(error);
      });
  } catch (err) {
    console.log(err);
  }
};
