import React, { useEffect } from "react";
import { useDrag } from "react-dnd";
import Avatar from "@mui/material/Avatar";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";

function Employee(props) {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "employee",
    item: { employee: props.employee, employees: props.employees },

    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));
  const style = {
    backgroundColor: "rgba(255, 255, 255, 0.3)",
    borderRadius: "20px",
  };
  useEffect(async () => {
    await slidesPlugin();
  });
  function slidesPlugin(activeSlide = 2) {
    const slides = document.querySelectorAll(".slide");

    slides[activeSlide].classList.add("active");

    for (const slide of slides) {
      slide.addEventListener("click", () => {
        clearActiveClasses();

        slide.classList.add("active");
      });
    }

    function clearActiveClasses() {
      slides.forEach((slide) => {
        slide.classList.remove("active");
      });
    }
  }

  useEffect(async () => {
    await slidesPlugin();
  });
  props.filteredEmployees.map(function (employee) {
    return (
      <div>
        <div className="container">
          <div
            className="slide"
            style={{
              backgroundImage:
                "url('https://images.unsplash.com/photo-1477764250597-dffe9f601ae8?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=2000&q=80')",
            }}
          >
            <h3>Tiger</h3>
          </div>{" "}
        </div>{" "}
      </div>
    );
  });
}

export default Employee;
