import React, { useState } from "react";
import Employee from "./Employee";
import Team from "./Team";
import Alliance from "./Alliance";
import { useSelector } from "react-redux";
import TextField from "@material-ui/core/TextField";
import { Button } from "@mui/material";
import { addNewTeam } from "../helpers/functionallity/addNewTeam";
import { Grid, CircularProgress } from "@material-ui/core";

import "../styles/App.css";

function DragDrop() {
  const [searchField, setsearchField] = useState("");
  const [NewTeamName, setNewTeamName] = useState("");
  const employees = useSelector((state) => state.employees);
  const teams = useSelector((state) => state.teams);
  const alliances = useSelector((state) => state.alliances);
  const filteredEmployees = employees.filter((emp) =>
    emp["Name"].toLowerCase().includes(searchField.toLowerCase())
  );

  console.log(teams, "TEAMS: ");

  return (
    <div className="dnd">
      <div>
        <TextField
          id="standard-basic"
          label="Search Employee"
          variant="standard"
          onChange={(e) => setsearchField(e.target.value)}
        />
        <div className="employees">
          {filteredEmployees.map((employee) => {
            return <Employee key={employee.id} employee={employee} />;
          })}
        </div>
      </div>
      <div>
        <div>
          <TextField
            id="standard-basic"
            label="Team Name"
            variant="standard"
            onChange={(e) => setNewTeamName(e.target.value)}
          />
          <Button
            onClick={() => {
              addNewTeam(NewTeamName);
            }}
          >
            Add Team
          </Button>
        </div>
        {!teams.length ? (
          <CircularProgress />
        ) : (
          <Grid container alignItems="stretch" spacing={3}>
            {teams.map((team) => (
              <Grid key={team.id} item xs={6} sm={6} md={6}>
                <Team team={team} key={team.id} />
              </Grid>
            ))}
          </Grid>
        )}
      </div>
      <div>
        {alliances.map((alliance) => {
          return <Alliance key={alliance.id} alliance={alliance} />;
        })}
      </div>
    </div>
  );
}

export default DragDrop;
