import React, { useState } from "react";
import ReactCardFlip from "react-card-flip";
import { useDrop, useDrag } from "react-dnd";
import { addEmployeeToTeam } from "../actions/employees";
import { deleteTeam } from "../actions/teams";
import { useDispatch } from "react-redux";
import Card from "@mui/material/Card";
import { Button } from "@mui/material";
import CardHeader from "@mui/material/CardHeader";
import DeleteTwoToneIcon from "@mui/icons-material/DeleteTwoTone";
import Avatar from "@mui/material/Avatar";

function Team(props) {
  const [isFlipped, setIsFlipped] = useState(false);

  const handleClick = () => {
    setIsFlipped(!isFlipped);
  };
  const dispatch = useDispatch();
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "employee",
    drop: (item) => {
      dispatch(addEmployeeToTeam(item.employee, props.team, props.employees));
    },
  }));
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "team",
    item: { team: props.team },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  return (
    // <div ref={drop}>
    //   <Card
    //     className="teams"
    //     key={props.team.id}
    //     ref={drag}
    //     style={{
    //       backgroundColor: "rgba(255, 255, 255, 0.3)",
    //       borderRadius: "20px",
    //     }}
    //   >
    //     <CardHeader title={props.team.Name} />

    //     <Button
    //       style={{
    //         opacity: "0.4",
    //         margin: "1vh",
    //       }}
    //       onClick={() => {
    //         dispatch(deleteTeam(props.team, props.teams));
    //       }}
    //     >
    //       <DeleteTwoToneIcon
    //         style={{
    //           position: "absolute",
    //           bottom: 55,
    //           right: -60,
    //           color: "red",
    //         }}
    //       />
    //     </Button>
    //   </Card>
    // </div>
    <ReactCardFlip isFlipped={isFlipped} flipDirection="horizontal">
      <div ref={drop} onClick={handleClick}>
        <Card
          className="teams"
          key={props.team.id}
          ref={drag}
          style={{
            backgroundColor: "rgba(255, 255, 255, 0.3)",
            borderRadius: "20px",
          }}
        >
          <CardHeader title={props.team.Name} />

          <Button
            style={{
              opacity: "0.4",
              margin: "1vh",
            }}
            onClick={() => {
              dispatch(deleteTeam(props.team, props.teams));
            }}
          >
            <DeleteTwoToneIcon
              style={{
                position: "absolute",
                bottom: 55,
                right: -60,
                color: "red",
              }}
            />
          </Button>
        </Card>
      </div>

      <Card
        style={{
          display: "inline",
        }}
        onClick={handleClick}
      >
        <div style={{ display: "flex", flexDirection: "row" }}>
          {props.team.employees.map((e) => {
            return (
              <div>
                <CardHeader
                  style={{ display: "flex" }}
                  avatar={<Avatar src={e["Avatar"]["url"]} />}
                />
              </div>
            );
          })}
        </div>
      </Card>
    </ReactCardFlip>
  );
}

export default Team;
