import * as helaperApi from "../Api/helperApis.js";

export const addEmployeeToTeam = async (employee, team, employees, teams) => {
  try {
    const employeeUpdated = await helaperApi.addEmployeeToTeam(employee, team);

    /////////////////////////////
     var employeesUpdated = team.employees.filter((e) => e.id !== employee.id);
     employeesUpdated = employeesUpdated.concat(employeeUpdated);
     var teamsUpdated = teams.filter((t) => t.id !== team.id);
 
    team.employees = employeesUpdated;
    console.log(team, "team");

    teamsUpdated = teamsUpdated.concat(team);

    console.log(teamsUpdated, "teamsUpdated");
    /////////////////////////////////

    return [employeesUpdated, teamsUpdated];
  } catch (err) {
    console.log(err);
  }
};
