import React from "react";
import { useDrop, useDrag } from "react-dnd";
import { addEmployeeToTeam } from "../helpers/functionallity/updateEmployee.js";
import { deleteTeam } from "../actions/teams";
import Card from "@mui/material/Card";
import { Button } from "@mui/material";
import CardHeader from "@mui/material/CardHeader";
import Avatar from "@mui/material/Avatar";
import { useSelector, useDispatch } from "react-redux";

import DeleteTwoToneIcon from "@mui/icons-material/DeleteTwoTone";
import { useState } from "react";
import ReactCardFlip from "react-card-flip";

const TeamInAlliance = (props) => {
  console.log(props, "PROPPPPPPPPPPPPPPSSSS");
  const [isFlipped, setIsFlipped] = useState(false);

  const handleClick = () => {
    setIsFlipped(!isFlipped);
  };
  const dispatch = useDispatch();

  const [{ isDragging }, drag] = useDrag(() => ({
    type: "team",
    item: { team: props.team },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));
  return (
    <div
      style={{
        height: "20%",
        marginBottom: "10px",
        width: "100%",
      }}
    >
      <ReactCardFlip isFlipped={isFlipped} flipDirection="horizontal">
        <div>
          <Card
            key={props.team.id}
            ref={drag}
            style={{
              backgroundColor: "rgba(255, 255, 255, 0.3)",
              borderRadius: "20px",
            }}
            onClick={handleClick}
          >
            <CardHeader title={props.team.Name} />
            <div>
              <div>
                <div
                  style={{
                    opacity: "0.4",
                    margin: "1vh",
                  }}
                >
                  <DeleteTwoToneIcon
                    style={{
                      position: "absolute",
                      bottom: 10,
                      right: 10,
                      color: "blue",
                    }}
                    onClick={() => {
                      dispatch(deleteTeam(props.team, props.teams));
                    }}
                  />
                </div>
              </div>
            </div>
          </Card>
        </div>

        <Card
          style={{
            display: "inline",
          }}
          onClick={handleClick}
        >
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              flexWrap: "wrap",
            }}
          >
            {props.team.employees.length === 0
              ? "No Employees Here"
              : props.team.employees.map((e) => {
                console.log(e, "eeeeeeeeeeeeeeee");
                if(e.Name)
                  return (
                    <div key={e.id}>
                      <CardHeader
                        style={{ display: "flex" }}
                        avatar={<Avatar src={e["Avatar"]["url"]} />}
                      />
                    </div>
                  );
                })}
          </div>
        </Card>
      </ReactCardFlip>
    </div>
  );
};

export default TeamInAlliance;
