import * as helaperApi from "../Api/helperApis.js";

export const addNewTeam = async (NewTeamName) => {
  try {
    const data = await helaperApi.createNewTeam(NewTeamName);
    return data;
  } catch (err) {
    console.log(err);
  }
};

export const deleteTeam = async (team, teams) => {
  try {
    await helaperApi.deleteTeam(team);
    const data = teams.filter((t) => {
      return t.id !== team.id;
    });
    return data;
  } catch (err) {
    console.log(err);
  }
};

export const addEmployeeToTeam = async (employee, team, employees, teams) => {
  try {
    const employeeUpdated = await helaperApi.addEmployeeToTeam(employee, team);

    var employeesUpdated = team.employees.filter((e) => e.id !== employee.id);
    employeesUpdated = employeesUpdated.concat(employeeUpdated);
    var teamsUpdated = teams.filter((t) => t.id !== team.id);
    const index = teams.findIndex((x) => x.id === team.id);
    team.employees = employeesUpdated;
    teamsUpdated.splice(index, 0, team);
    return teamsUpdated;
  } catch (err) {
    console.log(err);
  }
};
