import React from "react";
import { useDrop, useDrag } from "react-dnd";
import { addEmployeeToTeam } from "../helpers/functionallity/employeeTeam.js";
import { deleteTeam } from "../helpers/functionallity/deleteTeam.js";
function Team(props) {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "employee",
    drop: (item) => {
      addEmployeeToTeam(item.employee, props.team);
    },
  }));
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "team",
    item: { team: props.team },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));
  return (
    <div className="teams" key={props.team.id} ref={drop} ref={drag}>
      <div> {props.team.Name} </div>
      <button
        onClick={() => {
          dispatch(deleteTeam(teams, props.team.id));
        }}
      >
        X
      </button>
    </div>
  );
}

export default Team;
