import axios from "axios";
import store from "../../index";
import alliances from "../../reducers/alliances";
const addTeamToAllianceApi = async (team, alliance) => {
  var data = JSON.stringify({
    alliance: alliance.id,
  });
  console.log(team , team)
  const team_url = `http://cms.avertra.com/teams/${team.id}`;

  var config = {
    method: "put",
    url: team_url,
    headers: {
      Authorization: "Bearer " + store.getState().token,
      "Content-Type": "application/json",
    },
    data: data,
  };

  try {
    const teamUpdated = await axios(config);
    console.log(teamUpdated.data, "DATAAA");
    return teamUpdated.data;
  } catch (err) {
    console.error(err);
  }
};

const createNewTeam = async (NewTeamName) => {
  var data = JSON.stringify({
    Name: NewTeamName,
  });
  var config = {
    method: "post",
    url: "http://cms.avertra.com/teams",
    headers: {
      Authorization: "Bearer " + store.getState().token,
      "Content-Type": "application/json",
    },
    data: data,
  };
  try {
    const resp = await axios(config);
    return resp.data;
  } catch (err) {
    console.error(err);
    return {};
  }
};
const deleteTeam = async (team) => {
  var config = {
    method: "delete",
    url: `http://cms.avertra.com/teams/${team.id}`,
    headers: {
      Authorization: "Bearer " + store.getState().token,
    },
  };
  try {
    await axios(config);
  } catch (err) {
    console.error(err);
  }
};
const addEmployeeToTeam = async (employee, team, teams) => {
  const ids = employee["teams"].map((t) => {
    return t.id;
  });
  var data = JSON.stringify({
    teams: ids.concat(team.id),
  });

  const employee_url = `http://cms.avertra.com/employees/${employee.id}`;

  var config = {
    method: "put",
    url: employee_url,
    headers: {
      Authorization: "Bearer " + store.getState().token,
      "Content-Type": "application/json",
    },
    data: data,
  };

  try {
    const resp = await axios(config);
    return resp.data;
  } catch (err) {
    console.error(err);
  }
};
export { addTeamToAllianceApi, createNewTeam, deleteTeam, addEmployeeToTeam };
