import React, { useEffect } from "react";
import { getEmployees } from "./actions/employees";
import { getToken } from "./actions/token";
import { getTeams } from "./actions/teams";
import { getAlliances } from "./actions/alliances";
import { useDispatch } from "react-redux";
import { HTML5Backend } from "react-dnd-html5-backend";
import { DndProvider } from "react-dnd";
import DragDrop from "./components/DragDrop";
import "./styles/App.css";

function App() {
  const dispatch = useDispatch();

  useEffect(async () => {
    try {
      const token = await dispatch(getToken());
      const employees = await dispatch(getEmployees());
      const teams = await dispatch(getTeams());
      const alliances = await dispatch(getAlliances());
    } catch (err) {
      console.log(err);
    }
  }, [dispatch]);
  function slidesPlugin(activeSlide = 2) {
    const slides = document.querySelectorAll(".slide");

    slides[activeSlide].classList.add("active");

    for (const slide of slides) {
      slide.addEventListener("click", () => {
        clearActiveClasses();

        slide.classList.add("active");
      });
    }

    function clearActiveClasses() {
      slides.forEach((slide) => {
        slide.classList.remove("active");
      });
    }
  }
  return (
    <div>
      <DndProvider backend={HTML5Backend}>
        <div className="App">
          <DragDrop className="dnd" />
        </div>
      </DndProvider>
    </div>
  );
}

export default App;
