import React from "react";
import { useDrop, useDrag } from "react-dnd";
import { addEmployeeToTeam } from "../helpers/functionallity/employeeTeam.js";
import { deleteTeam } from "../actions/teams";
import { useDispatch } from "react-redux";
import Card from "@mui/material/Card";
import { Button } from "@mui/material";
import CardHeader from "@mui/material/CardHeader";
import Add from "@material-ui/icons/Add";
import { Badge, IconButton, Typography } from "@material-ui/core";
import DeleteIcon from "@mui/icons-material/Delete";
import DeleteTwoToneIcon from "@mui/icons-material/DeleteTwoTone";

function Team(props) {
  const dispatch = useDispatch();

  const [{ isOver }, drop] = useDrop(() => ({
    accept: "employee",
    drop: (item) => {
      addEmployeeToTeam(item.employee, props.team);
    },
  }));
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "team",
    item: { team: props.team },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));
  return (
    <Card
      className="teams"
      key={props.team.id}
      ref={drop}
      ref={drag}
      style={{ backgroundColor: "rgba(255, 255, 255, 0.3)" }}
    >
      <CardHeader title={props.team.Name} />

      <Button
        variant={"contained"}
        data-testid="DeleteIcon"
        style={{
          height: "20",
          width: "20",
          opacity: "0.4",
          margin: "1vh",
          position: "absolute",
          bottom: 50,
          right: 0,
        }}
        onClick={() => {
          dispatch(deleteTeam(props.team, props.teams));
        }}
      >
        <DeleteTwoToneIcon
          style={{ position: "absolute", bottom: 50, right: 0 }}
        />
      </Button>
    </Card>
  );
}

export default Team;
