import React, { useState } from "react";
import Employee from "./Employee";
import Team from "./Team";
import Alliance from "./Alliance";
import { useSelector } from "react-redux";
import TextField from "@material-ui/core/TextField";
import { useDrop } from "react-dnd";

import "../styles/App.css";
function DragDrop() {
  const [searchField, setsearchField] = useState("");
  const employees = useSelector((state) => state.employees);
  const teams = useSelector((state) => state.teams);
  function collect(connect, monitor) {
    return {
      connectDragSource: connect.dragSource(),
      isDragging: monitor.isDragging(),
    };
  }
  const [{ isOver }, drop, collectedProps] = useDrop(() => ({
    accept: "employee",
    collect: (monitor, props) => collect(connect, monitor),
    drop: (e) => addImageToBoard(e),
  }));
  console.log(collectedProps, "1111111111111111");
  const addImageToBoard = (id) => {
    console.log(id, "iddddddd");
  };
  const filteredEmployees = employees.filter((emp) =>
    emp["Name"].toLowerCase().includes(searchField.toLowerCase())
  );

  return (
    <div>
      <TextField
        id="standard-basic"
        label="Search Employee"
        variant="standard"
        onChange={(e) => setsearchField(e.target.value)}
      />
      <div className="employees">
        {filteredEmployees.map((employee) => {
          return <Employee key={employee.id} employee={employee} />;
        })}
      </div>
      <div className="teams" ref={drop}>
        {teams.map((team) => {
          return <Team key={team.id} team={team} />;
        })}
      </div>
    </div>
  );
}

export default DragDrop;
