import React, { useEffect, useState } from "react";
import { useDrop } from "react-dnd";
import { addTeamToAlliance } from "../helpers/functionallity/teamAlliance";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import { getTeamBelongToThisAlliance } from "../helpers/Api/helperApis.js";

function Alliance(props) {
  const [teams, setTeams] = useState(
    JSON.parse(localStorage.getItem(`${props.alliance.id}`)) || []
  );
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "team",
    drop: (item) => {
      addTeamToAlliance(item.team, props.alliance);
      getTeamBelongToThisAlliance(props.alliance);

      setTeams(JSON.parse(localStorage.getItem(`${props.alliance.id}`)));
    },
  }));

  useEffect(async () => {
    getTeamBelongToThisAlliance(props.alliance);
  }, []);
  useEffect(async () => {
    console.log("111111111111111")
    getTeamBelongToThisAlliance(props.alliance);
  }, [teams]);
  return (
    <List
      className="alliance"
      ref={drop}
      sx={{
        width: "100%",
        maxWidth: 360,
        bgcolor: "background.paper",
        marginLeft: "50px",
        marginBottom: "50px",
      }}
    >
      {teams.map((team) => {
        return (
          <ListItem alignItems="flex-start">
            <ListItemAvatar>
              <Avatar alt={team.Name[0]} src="/static/images/avatar/1.jpg" />
            </ListItemAvatar>
            <ListItemText primary={team.Name} />
          </ListItem>
        );
      })}
    </List>
  );
}

export default Alliance;
