import React from "react";
import { useDrop, useDrag } from "react-dnd";
import { addEmployeeToTeam } from "../helpers/functionallity/updateEmployee.js";
import { deleteTeam } from "../actions/teams";
import { useDispatch } from "react-redux";
import Card from "@mui/material/Card";
import { Button } from "@mui/material";
import CardHeader from "@mui/material/CardHeader";
import Avatar from "@mui/material/Avatar";

import DeleteTwoToneIcon from "@mui/icons-material/DeleteTwoTone";
import ReactDOM from "react-dom";
import { useState } from "react";
import ReactCardFlip from "react-card-flip";

const TeamInAlliance = (props) => {
  console.log(props.team, "CCCCCC");
  const [isFlipped, setIsFlipped] = useState(false);

  const handleClick = () => {
    setIsFlipped(!isFlipped);
  };
  const dispatch = useDispatch();
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "employee",
    drop: (item) => {
      dispatch(addEmployeeToTeam(item.employee, props.team, props.employees));
    },
  }));
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "team",
    item: { team: props.team },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));
  return (
    <div
      ref={drop}
      style={{
        height: "20%",
        marginBottom: "10px",
        width: "100%",
      }}
    >
      <ReactCardFlip isFlipped={isFlipped} flipDirection="horizontal">
        <div>
          <Card
            key={props.team.id}
            ref={drag}
            style={{
              backgroundColor: "rgba(255, 255, 255, 0.3)",
              borderRadius: "20px",
            }}
            onClick={handleClick}
          >
            <CardHeader title={props.team.Name} />
            <div style={{}}>
              <div>
                <Button
                  style={{
                    opacity: "0.4",
                    margin: "1vh",
                  }}
                  onClick={() => {
                    dispatch(deleteTeam(props.team, props.teams));
                  }}
                >
                  <DeleteTwoToneIcon
                    style={{
                      position: "absolute",
                      bottom: 55,
                      right: -60,
                      color: "red",
                    }}
                  />
                </Button>
              </div>
            </div>
          </Card>
        </div>

        <Card
          style={{
            fontWeight: "bold",
            display: "inline",
            flexDirection: "row",
            justifyContent: "space-between",
          }}
          onClick={handleClick}
        >
          <div className="teamsInAlliance" ref={drop}>
            {props.team.employees.map((e) => {
              return (
                <div>
                  <CardHeader avatar={<Avatar src={e["Avatar"]["url"]} />} />
                </div>
              );
            })}
          </div>
        </Card>
      </ReactCardFlip>
    </div>
  );
};

export default TeamInAlliance;
