import React, { useState, useEffect } from "react";
import Employee from "./Employee";
import Team from "./Team";
import Alliance from "./Alliance";
import { useSelector, useDispatch } from "react-redux";
import TextField from "@material-ui/core/TextField";
import { Button } from "@mui/material";
import { AddTeam } from "../actions/teams";
import Grid from "@mui/material/Grid";
import GridList from "@material-ui/core/GridList";
import GridListTile from "@material-ui/core/GridListTile";
import GridListTileBar from "@material-ui/core/GridListTileBar";
import "../styles/App.css";
function DragDrop() {
  const dispatch = useDispatch();
  const teams = useSelector((state) => {
    return state.teams;
  });
  var t = teams.filter((t) => {
    return t.alliance === undefined;
  });
  console.log(teams, "TEAMS Drag and DROP");
  console.log(t, "t Drag and DROP");
  const [searchField, setsearchField] = useState("");
  const [NewTeamName, setNewTeamName] = useState("");
  const employees = useSelector((state) => state.employees);
  const alliances = useSelector((state) => state.alliances);
  const filteredEmployees = employees.filter((emp) =>
    emp["Name"].toLowerCase().includes(searchField.toLowerCase())
  );

  return (
    <Grid
      container
      direction="column"
      justifyContent="space-around"
      alignItems="stretch"
    >
      {/* <div className="dnd"> */}
      <Grid item xs>
        <TextField
          id="standard-basic"
          fullWidth
          label="Search Employee"
          variant="standard"
          onChange={(e) => setsearchField(e.target.value)}
        />
        <GridList className="gridList" cols={2.5}>
          <div className="employees">
            {filteredEmployees.map((employee) => {
              <GridListTile key={employee.id}>
                return <Employee key={employee.id} employee={employee} />;
              </GridListTile>;
            })}
          </div>
        </GridList>
      </Grid>
      <div>
        <Grid item xs>
          <TextField
            id="standard-basic"
            label="Team Name"
            variant="standard"
            value={NewTeamName}
            onChange={(e) => {
              setNewTeamName(e.target.value);
            }}
          />
          <Button
            onClick={() => {
              dispatch(AddTeam(teams, NewTeamName));
              setNewTeamName("");
            }}
          >
            Add Team
          </Button>
        </Grid>
        <Grid item xs>
          {t.map((team) => {
            return <Team key={team.id} team={team} />;
          })}
        </Grid>
      </div>
      <div>
        {alliances.map((alliance) => {
          return (
            <Alliance key={alliance.id} alliance={alliance} teams={teams} />
          );
        })}
      </div>
      {/* </div> */}
    </Grid>
  );
}

export default DragDrop;
