import React, { useEffect, useState } from "react";
import { useDrop, useDrag } from "react-dnd";
import { addTeamToAlliance } from "../actions/teams";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import store from "../index";
import { useDispatch } from "react-redux";
import TeamInAlliance from "./TeamInAlliance";
import { Container, Col, Card } from "react-bootstrap";
import { DataGrid } from "@mui/x-data-grid";
import { color } from "@mui/system";

function Alliance(props) {
  const dispatch = useDispatch();
  const [teams, setTeams] = useState(props.teams);

  const [{ isOver }, drop] = useDrop(() => ({
    accept: "team",
    drop: (item) => {
      console.
      dispatch(addTeamToAlliance(item.team, props.alliance, teams));
    },
  }));

  return (
    <div ref={drop}>
      <Card
        style={{
          backgroundColor: "rgba(255, 255, 255, 0.3)",
          borderRadius: "20px",
        }}
      >
        {props.alliance.Name}
        <Container className="teamsInAlliance">
          {props.alliance.teams.map((team) => {
            return (
              <TeamInAlliance
                key={team.id}
                alliance={props.alliance}
                team={team}
                teams={teams}
              />
            );
          })}
        </Container>
      </Card>
    </div>
  );
}
export default Alliance;
